DROP TABLE IF EXISTS `blog`;
CREATE TABLE `blog`  (
  `id` varchar(32) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '博客id',
  `title` varchar(200) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '文章标题',
  `summary` varchar(200) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '文章简介',
  `image` varchar(200) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '文章标题图片',
  `content_md` longtext CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT 'markdown格式内容',
  `content_html` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT 'html格式内容',
  `sort_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '分类id',
  `click_count` int(11) NULL DEFAULT 0 COMMENT '文章点击数',
  `user_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '作者',
  `is_top` tinyint(1) NULL DEFAULT NULL COMMENT '是否置顶 0：否 1：是',
  `is_original` tinyint(1) NULL DEFAULT 1 COMMENT '是否原创 0：否 1：是',
  `articles_part` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '转载地址',
  `blog_level` int(11) NOT NULL DEFAULT 0 COMMENT '推荐等级 0默认',
  `blog_status` tinyint(1) NULL DEFAULT 0 COMMENT '状态 0：未发布  1：已发布',
  `start_comment` tinyint(1) NULL DEFAULT 1 COMMENT '是否开启评论 0：否 1是',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `sort_id`(`sort_id`) USING BTREE,
  INDEX `user_id`(`user_id`) USING BTREE,
  CONSTRAINT `blog_ibfk_1` FOREIGN KEY (`sort_id`) REFERENCES `sort` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `blog_ibfk_2` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '博客表' ROW_FORMAT = Compact;

